from stores.Portativ.portativ_info import PORTATIV_ARTICLESfrom stores.Portativ.portativ_model import PortativStoredef start_portativ_wacom():    portativ = PortativStore(url='https://portativ.ua/ua/catalogsearch/result/?q=wacom')    items = portativ.generate_info()    return portativ.compare_data(items)def start_portativ_xp_pen():    portativ = PortativStore(url='https://portativ.ua/ua/catalogsearch/result/?q=xp-pen')    items = portativ.generate_info()    return portativ.compare_data_xp_pen(items, PORTATIV_ARTICLES)