from redis_client.redis import redis_clientfrom stores.Rozetka.rozetka_model import RozetkaStorefrom tasks.client import celery_appwacom = 'https://rozetka.com.ua/search/?producer=wacom&redirected=1&seller=rozetka&text=wacom'xp_pen = 'https://rozetka.com.ua/ua/search/?producer=xp-pen&redirected=1&section_id=83199&seller=rozetka&text=xp+pen'@celery_app.taskdef start_rozetka_wacom(url):    rozetka = RozetkaStore()    items = (rozetka.generate_info(url))    result = rozetka.compare_data(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='rozetka', value=json_data)    return result@celery_app.taskdef start_rozetka_xp_pen(url):    rozetka = RozetkaStore()    items = (rozetka.generate_info(url))    result = rozetka.compare_data_xp_pen(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='rozetka_xp', value=json_data)    return resultprint(start_rozetka_xp_pen(xp_pen))