from playwright.sync_api import sync_playwrightfrom Classes.status import Statusfrom Classes.store import BaseStorefrom utillities import clean_priceclass RozetkaStore(BaseStore):    def __init__(self):        super().__init__(            shop_url='https://rozetka.com.ua/ua/search/?producer=wacom&redirected=1&seller=rozetka_only&text=wacom'        )        self.item_list = []    def generate_info(self, url, articles_dict):        with sync_playwright() as p:            browser = p.chromium.launch(headless=True)            page = browser.new_page()            page.goto(url)            page.wait_for_selector('.goods-tile', timeout=5000)            items = page.locator('.goods-tile')            items_count = items.count()            if items_count == 0:                print("Товары не найдены на странице.")            for i in range(items_count):                try:                    item = items.nth(i)                    title_element = item.locator('.product-link.goods-tile__heading')                    title_text = title_element.inner_text().strip().replace("Офіційна гарантія 24 міс.", "")                    article = articles_dict.get(title_text)                    price_new_elements = item.locator('.goods-tile__price-value')                    price_old_elements = item.locator('.goods-tile__price--old')                    status_elements = item.locator('.goods-tile__availability')                    status = Status.in_stock                    availability_text = status_elements.inner_text()                    if 'Немає в наявності' in availability_text:                        status = Status.not_in_stock                    price = 'N/A'                    if price_new_elements.count() > 0:                        price = price_new_elements.inner_text()                    elif price_old_elements.count() > 0:                        price = price_old_elements.inner_text()                    card_item = {                        'name': title_text.replace("Офіційна гарантія 24 міс.", ""),                        'price': clean_price(price) if price != 'N/A' else price,                        'article': article,                        'status': status                    }                    self.item_list.append(card_item)                except Exception as e:                    print(f'Ошибка при обработке элемента: {e}')            browser.close()        return self.item_list