import osimport djangoos.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()from scraper.celery_config import appfrom stores.Rozetka.info import ROZETKA_ARTICLES_WACOM, ROZETKA_ARTICLES_XP_PENfrom stores.Rozetka.rozetka_model import RozetkaStorewacom = 'https://rozetka.com.ua/search/?producer=wacom&redirected=1&seller=rozetka&text=wacom'xp_pen = 'https://rozetka.com.ua/ua/search/?producer=xp-pen&redirected=1&section_id=83199&seller=rozetka&text=xp+pen'@app.task()def start_rozetka_wacom():    rozetka = RozetkaStore()    items = (rozetka.generate_info(url=wacom, articles_dict=ROZETKA_ARTICLES_WACOM))    if not items:        rozetka.send_allert_notification()    rozetka.save_parsed_data(partner_name='Rozetka', items=items, brand="WACOM")@app.task()def start_rozetka_xp_pen():    rozetka = RozetkaStore()    items = (rozetka.generate_info(url=xp_pen, articles_dict=ROZETKA_ARTICLES_XP_PEN))    if not items:        rozetka.send_allert_notification()    rozetka.save_parsed_data(partner_name='Rozetka', items=items, brand="XP-Pen")