from redis_client.redis_client import redis_clientfrom stores.Setevuha.setevuha_model import SetevuhaStorefrom tasks.client import celery_app@celery_app.taskdef start_setevuha():    setevuha = SetevuhaStore()    items = setevuha.generate_info()    result = setevuha.compare_data(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='setevuha', value=json_data)    return result