import requestsfrom bs4 import BeautifulSoupfrom Classes.store import BaseStorefrom utillities import get_article_from_titleclass SetevuhaStore(BaseStore):    def __init__(self, shop_url= 'https://setevuha.com.ua/ru/brands/wacom/'):        super().__init__(shop_url)        self.soup = self.get_soup()        self.item_list = []        self.all_items = self.soup.find(class_='row row-cols-4 gy-4 gy-md-4 gx-0 gx-md-4 row-cols-4')    def get_soup(self):        response = requests.get('https://setevuha.com.ua/ru/brands/wacom/', verify=False)        response.raise_for_status()        return BeautifulSoup(response.text, 'lxml')    def generate_info(self):        for item in self.all_items.find_all(class_='col-12 col-md-6 col-xxl-4 col-xxxl-3'):            price = item.find(class_="price")            name = item.find(class_='card-title')            if price is not None:                price = price.text.strip().split('₴')[0].strip().replace(' ', '')            if name is not None:                name = name.text.strip()                article = get_article_from_title(name)                if name == 'Графический планшет Wacom (PTH460KOB)':                   article = 'PTH460K0B'            card_item = {                'name': name,                'price': price,                'article': article            }            self.item_list.append(card_item)        return self.item_list