import osimport djangofrom scraper.celery_config import appfrom stores.WO.Wo_model import WoStorefrom stores.WO.tasks import wacom_page_urls, WO_WACOM_ARTICLES, xp_pen_page_urls, WO_XP_PEN_ARTICLESos.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()@app.task()def start_wo_wacom():    wo = WoStore(wacom_page_urls)    items = wo.generate_info(articles_dict=WO_WACOM_ARTICLES)    return items@app.task()def start_wo_xp_pen():    wo = WoStore(xp_pen_page_urls)    items = wo.generate_info(articles_dict=WO_XP_PEN_ARTICLES)    return items