import osimport djangofrom redis_client.redis_client import redis_clientfrom stores.WO.Wo_model import WoStorefrom stores.WO.wo_info import wacom_page_urls, WO_WACOM_ARTICLES, xp_pen_page_urls, WO_XP_PEN_ARTICLESos.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()from items.models import Itemdef start_wo_wacom():    wo = WoStore(wacom_page_urls)    items = wo.generate_info()    result = wo.compare_data_xp_pen(partner_items_list=items, article_dict=WO_WACOM_ARTICLES, model=Item)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='wo_wacom', value=json_data)    return resultdef start_wo_xp_pen():    wo = WoStore(xp_pen_page_urls)    items = wo.generate_info()    result = wo.compare_data_xp_pen(partner_items_list=items, article_dict=WO_XP_PEN_ARTICLES, model=Item)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='wo_xp_pen', value=json_data)    return result