from redis_client.redis import redis_clientfrom stores.WO.Wo_model import WoStorefrom stores.WO.wo_info import wacom_page_urls, WO_WACOM_ARTICLES, xp_pen_page_urls, WO_XP_PEN_ARTICLESfrom tasks.client import celery_app@celery_app.taskdef start_wo_wacom():    wo = WoStore(wacom_page_urls)    items = wo.generate_info()    result = wo.compare_all_data(partner_list=items, articles_list=WO_WACOM_ARTICLES)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='wo_wacom', value=json_data)    return result@celery_app.taskdef start_wo_xp_pen():    wo = WoStore(xp_pen_page_urls)    items = wo.generate_info()    result = wo.compare_all_data(partner_list=items, articles_list=WO_XP_PEN_ARTICLES)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='wo_xp_pen', value=json_data)    return result