import requestsfrom bs4 import BeautifulSoupfrom Classes.status import Statusfrom Classes.store import BaseStorefrom utillities import get_article_from_title, clean_priceclass WoStore(BaseStore):    def __init__(self, urls):        super().__init__(shop_url='https://wo.ua/ua/search/?q=wacom&page=1')        self.urls = urls        self.all_items = []        for url in self.urls:            soup = self.get_soup(url)            catalog_content = soup.find(class_='filtered-items')            if catalog_content:                self.all_items.append(catalog_content)            else:                print(f'No filtered-items found on page: {url}')    def get_soup(self, url):        response = requests.get(url)        response.raise_for_status()        return BeautifulSoup(response.text, 'lxml')    def generate_info(self, articles_dict):        self.item_list = []        try:            for items in self.all_items:                for item in items.find_all('div', class_='product-item ecom_item'):                    price_element = item.find('div', class_='product-item__bottom-price-new')                    status_elem = item.find(class_='product-item__bottom-cart')                    name_element = item.find('a', class_='product-item__name')                    if price_element and name_element:                        name = name_element.text.strip()                        article_elem = articles_dict.get(name)                        price = price_element.text.strip().replace('.00', '')                        card_item = {                            'name': name,                            'price': clean_price(price),                            'article': article_elem if article_elem else None,                            'status': Status.in_stock if status_elem else Status.not_in_stock                        }                        self.item_list.append(card_item)        except Exception as e:            print(f"An error occurred: {e}")            return []        return self.item_list