import loggingimport osimport djangofrom scraper.celery_config import appos.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()from stores.WacomStore.wacomsotre_info import url, urlsfrom stores.WacomStore.wacomstore_model import WacomStorelogger = logging.getLogger('scraper')@app.taskdef start_wacom_store_wacom():    try:        wacom = WacomStore()        items = wacom.generate_info_with_many_links(links=urls)        if not items:            logger.info(f"Не вдалось спарсити данні WacomStore/WACOM - {len(items)} товарів")            wacom.send_allert_notification()        logger.info(f"Збережено/Оновлено {len(items)} товарів для партнера WacomStore (бренд WACOM).")        wacom.save_parsed_data(partner_name='WacomStore', items=items, brand="WACOM")    except Exception as e:        logger.info(f"Помилка парсингу данних WacomStore/WACOM {e}")