import loggingimport osimport djangofrom scraper.celery_config import appos.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()from stores.WacomStore.wacomsotre_info import WacomStoreLocatorfrom stores.WacomStore.wacomstore_model import WacomStorelogger = logging.getLogger('scraper')@app.taskdef start_wacom_store_wacom():    try:        wacom = WacomStore()        items = wacom.generate_info_with_many_links(links=WacomStoreLocator.urls,                                                    products_link_locator=WacomStoreLocator.items_main_block,                                                    name_elem_locator=WacomStoreLocator.name_elem,                                                    price_elem_locator=WacomStoreLocator.price_elem,                                                    article_elem_locator=WacomStoreLocator.article_elem)        if not items:            logger.info(f"Не вдалось спарсити данні WacomStore/WACOM - {len(items)} товарів")            wacom.send_allert_notification()        logger.info(f"Збережено/Оновлено {len(items)} товарів для партнера WacomStore (бренд WACOM).")        wacom.save_parsed_data(partner_name='WacomStore', items=items, brand="WACOM")    except Exception as e:        logger.info(f"Помилка парсингу данних WacomStore/WACOM {e}")