import requestsfrom bs4 import BeautifulSoupfrom Classes.store import BaseStorefrom stores.WacomStore.wacomsotre_info import headersclass WacomStore(BaseStore):    def __init__(self, url):        super().__init__(shop_url='https://wacomstore.com.ua/')        self.url = url        soup = self.get_soup(self.url)        self.items = soup.find_all(class_='product')    def get_soup(self, link):        response = requests.get(link, headers=headers)        response.raise_for_status()        return BeautifulSoup(response.text, 'lxml')    def generate_info(self):        try:            for item in self.items:                name_elem = item.find(class_='name')                price_elem = item.find(class_='price-value')                article_elem = item.find(class_='articul-name')                if price_elem:                    price = price_elem.text.strip().split('â‚´')[0].strip().replace(' ', '')                else:                    price = 1                if article_elem:                    article = article_elem.text.strip()                else:                    article = 'None'                card_item = {                    'name': name_elem.text.strip() if name_elem else "N/A",                    'price': price,                    'article': article                }                self.item_list.append(card_item)        except Exception as ex:            return f'{ex}'        return self.item_list