import asyncioimport loggingimport osfrom aiogram import Bot, Dispatcher, typesfrom aiogram.filters import Commandfrom dotenv import load_dotenvfrom databases.db_helper import schedule_updatesfrom telegram_bot.handlers import user_routerfrom telegram_bot.keyboards import get_custom_keyboardfrom telegram_bot.tg_users import Userload_dotenv()token = os.getenv(key='BOT_TOKEN_TEST')bot = Bot(token=token)dp = Dispatcher(bot=bot)dp.include_router(user_router)logging.basicConfig(level=logging.INFO)@dp.message(Command(commands=["start"]))async def start(message: types.Message):    user = User.from_telegram_user(message.from_user, message.chat.id)    print(user.__dict__)    await message.answer(f"Привіт, {message.from_user.username}!✋:")    await message.answer(f"Тисни на кнопку  ⬇️ ⬇️ ⬇️", reply_markup=get_custom_keyboard())async def main():    update_task = asyncio.create_task(schedule_updates())    await dp.start_polling(bot, skip_updates=True)    await update_task