import asyncioimport loggingimport osfrom aiogram import Bot, Dispatcher, typesfrom dotenv import load_dotenvfrom databases.db_helper import schedule_updatesfrom telegram_bot.comands_handlers import comand_routerfrom telegram_bot.handlers import user_routerload_dotenv()token = os.getenv(key='BOT_TOKEN_TEST')bot = Bot(token=token)dp = Dispatcher(bot=bot)dp.include_router(user_router)dp.include_router(comand_router)logging.basicConfig(level=logging.INFO)async def main():    update_task = asyncio.create_task(schedule_updates())    await dp.start_polling(bot, skip_updates=True)    await update_task