import asyncioimport loggingfrom config import BOT_TOKENfrom aiogram import Bot, Dispatcher, typesfrom aiogram.filters import Commandfrom Foxtrot.foxtrot import start_foxtrotfrom KTC.ktc import start_ktcfrom Moyo.moyo import start_moyobot = Bot(token=BOT_TOKEN)dp = Dispatcher(bot=bot)logging.basicConfig(level=logging.INFO)@dp.message(Command(commands=["start"]))async def start(message: types.Message):    kb = [        [types.KeyboardButton(text="Foxtrot")],        [types.KeyboardButton(text="Moyo")],        [types.KeyboardButton(text="KTC")]    ]    keyboard = types.ReplyKeyboardMarkup(keyboard=kb, resize_keyboard=True)    await message.answer("Choose a shop to check prices:", reply_markup=keyboard)@dp.message()async def handle_message(message: types.Message):    if message.text == "Foxtrot":        results = start_foxtrot()        result_str = '\n'.join([str(item) for item in results])        await message.answer(f"üõíFoxtrot:\n\n{result_str}")    elif message.text == "Moyo":        results = start_moyo()        result_str = '\n'.join([str(item) for item in results])        await message.answer(f"üõí Moyo:\n\n{result_str}")    elif message.text == "KTC":        results = start_ktc()        result_str = '\n'.join([str(item) for item in results])        await message.answer(f"üõí KTC:\n\n{result_str}")    else:        await message.answer("Please choose a shop from the keyboard.")async def main():    await dp.start_polling(bot, skip_updates=True)if __name__ == "__main__":    asyncio.run(main())# @dp.message(Command(commands=['start']))# async def start(message: types.Message):#     await message.reply(f'üëã–ü—Ä–∏–≤—ñ—Ç, {message.from_user.username}')#     time.sleep(3)#     await message.answer('–î–∞ –¥–∞–Ω–∏–π –º–æ–º–µ–Ω—Ç —è –º–æ–∂—É –ø–µ—Ä–µ–≤—ñ—Ä–∏—Ç—ñ —Ü—ñ–Ω–∏ –Ω–∞ Wacom!')#     await message.answer(f'–≤–≤–µ–¥–∏ "/" —Ç–∞ –Ω–∞–∑–≤—É –º–∞–≥–∞–∑–∏–Ω—É –∑—ñ —Å–ø–∏—Å–∫—É')#     await message.answer('fox