from aiogram import types, Routerfrom main import start_shops_checkinguser_router = Router()@user_router.message()async def all_messages(message: types.Message):    await message.reply('hi')@user_router.message(comands=['start'])async def start(message: types.Message):    await message.reply('Hi i"m your helper, i know 3 shops, and can checking price.')@user_router.message.handlers(comands=['check'])async def check_data(message: types.Message):    print(start_shops_checking())    await message.reply('wait')