from aiogram import types, Routerfrom aiogram.enums import ParseModefrom Auchan.auchan import start_auchanfrom CAN.can import start_canfrom Citrus.citrus import start_citrusfrom Click.click import start_click_xp_pen, start_click_wacomfrom F.fotos import start_fotosfrom Foxtrot.foxtrot import start_foxtrot, start_foxtrot_xp_penfrom Itbox.itbox import start_itboxfrom KTC.ktc import start_ktc, start_ktc_xp_penfrom MTA.mta import start_mtafrom Moyo.moyo import start_moyofrom Rozetka.rozetka import wacom, xp_pen, start_rozetka_xp_pen, start_rozetka_wacomfrom Setevuha.setevuha import start_setevuhafrom WO.Wo import start_wo_wacom, start_wo_xp_penuser_router = Router()@user_router.message(lambda message: message.text == "Foxtrot")async def foxtrot_handler(message: types.Message):    await message.answer("⏳ готую звіт Wacom + XP-Pen")    results_wacom = start_foxtrot()    results_xp_pen = start_foxtrot_xp_pen()    result_wacom_str = '\n'.join([str(item) for item in results_wacom])    await message.answer(f"🛒Foxtrot:\n\n{result_wacom_str}")    result_xp_pen_str = '\n'.join([str(item) for item in results_xp_pen])    await message.answer(f"🛒Foxtrot:\n\n{result_xp_pen_str}")@user_router.message(lambda message: message.text == "Moyo")async def moyo_handler(message: types.Message):    await message.answer("⏳ готую звіт...")    results = start_moyo()    result_str = '\n'.join([str(item) for item in results])    await message.answer(f"🛒 Moyo:\n\n{result_str}")@user_router.message(lambda message: message.text == "KTC")async def ktc_handler(message: types.Message):    await message.answer("⏳ готую звіт...")    results = start_ktc()    result_str = '\n'.join([str(item) for item in results])    results_xp_pen = start_ktc_xp_pen()    results_xp_pen_str = '\n'.join([str(item) for item in results_xp_pen])    await message.answer(f"🛒 KTC - WACOM:\n\n{result_str}")    await message.answer(f"🛒 KTC: - XP-Pen\n\n{results_xp_pen_str}")@user_router.message(lambda message: message.text == "Click")async def click_handler(message: types.Message):    await message.answer("⏳ готую звіт Wacom/XP-Pen")    results_wacom = start_click_wacom()    results_xp_pen = start_click_xp_pen()    result_str_wacom = '\n'.join([str(item) for item in results_wacom])    result_str_xp = '\n'.join([str(item) for item in results_xp_pen])    await message.answer(f"🛒 Click WACOM:\n\n{result_str_wacom}")    await message.answer(f"🛒 Click XP-PEN:\n\n{result_str_xp}")@user_router.message(lambda message: message.text == "Rozetka")async def rozetka_handler(message: types.Message):    await message.answer("⏳ готую звіт по WACOM та XP-Pen - це може зайняти 10-15 секунд....")    results_wacom = start_rozetka_wacom(wacom)    results_xp_pen = start_rozetka_xp_pen(xp_pen)    result_str_wacom = '\n'.join([str(item) for item in results_wacom])    result_str_xp_pen = '\n'.join([str(item) for item in results_xp_pen])    await message.answer(f"🛒 Rozetka WACOM:\n\n{result_str_wacom}")    await message.answer(f"🛒 Rozetka XP-PEN:\n\n{result_str_xp_pen}")@user_router.message(lambda message: message.text == "Auchan")async def auchan_handler(message: types.Message):    await message.answer("⏳ готую звіт... ")    results = start_auchan()    result_str = '\n'.join([str(item) for item in results])    await message.answer(f"🛒 Auchan:\n\n{result_str}", parse_mode=ParseMode.HTML)@user_router.message(lambda message: message.text == "WO")async def wo_handler(message: types.Message):    await message.answer("⏳ готую звіт...")    results_wacom = start_wo_wacom()    results_xp_pen = start_wo_xp_pen()    result_str_wacom = '\n'.join([str(item) for item in results_wacom])    result_str_xp_pen = '\n'.join([str(item) for item in results_xp_pen])    await message.answer(f"🛒 WO WACOM(товари, які є в наявності):\n\n{result_str_wacom}")    await message.answer(f"🛒 WO XP-Penт(товари, які є в наявності):\n\n{result_str_xp_pen}")@user_router.message(lambda message: message.text == "Setevuha")async def setevuha_handler(message: types.Message):    await message.answer("⏳ готую звіт...")    results = start_setevuha()    result_str = '\n'.join([str(item) for item in results])    await message.answer(f"🛒 Setevuha:\n\n{result_str}", parse_mode=ParseMode.HTML)@user_router.message(lambda message: message.text == "CAN")async def can_handler(message: types.Message):    await message.answer("⏳ готую звіт...")    results = start_can()    result_str = '\n'.join([str(item) for item in results])    await message.answer(f"🛒CAN:\n\n{result_str}")@user_router.message(lambda message: message.text == "ItBox")async def itbox_handler(message: types.Message):    await message.answer("⏳ готую звіт...")    results = start_itbox()    result_str = '\n'.join([str(item) for item in results])    await message.answer(f"🛒ItBox:\n\n{result_str}")@user_router.message(lambda message: message.text == "Citrus")async def citrus_handler(message: types.Message):    await message.answer("⏳ готую звіт...")    results = start_citrus()    result_str = '\n'.join([str(item) for item in results])    await message.answer(f"🛒Citrus:\n\n{result_str}")@user_router.message(lambda message: message.text == "F.ua")async def Fotos_handler(message: types.Message):    await message.answer("⏳ готую звіт...5-10 сек.")    results = start_fotos()    result_str = '\n'.join([str(item) for item in results])    await message.answer(f"🛒F.ua:\n\n{result_str}")@user_router.message(lambda message: message.text == "MTA")async def mta_handler(message: types.Message):    await message.answer("⏳ готую звіт...5-10 сек.")    results = start_mta()    result_str = '\n'.join([str(item) for item in results])    await message.answer(f"🛒MTA:\n\n{result_str}")