import datetimeclass User:    def __init__(self,                 username, first_name, full_name, telegram_id, last_name,                 chat_id, created_at=None, is_admin=False):        self.username = username        self.first_name = first_name        self.full_name = full_name        self.last_name = last_name        self.telegram_id = telegram_id        self.chat_id = chat_id        self.created_at = datetime.datetime.utcnow().strftime("%d %B %Y %H:%M:%S")        self.admin = is_admin    @staticmethod    def from_telegram_user(telegram_user, chat_id):        full_name = f"{telegram_user.first_name} {telegram_user.last_name}".strip()        return User(            username=telegram_user.username,            first_name=telegram_user.first_name,            full_name=full_name,            telegram_id=telegram_user.id,            last_name=telegram_user.last_name,            chat_id=chat_id,            is_admin=False        )